import { useState, useContext, useEffect } from "react";
import { StateContext } from "./context";
import { useResource } from "react-request-hook";

export default function Login() {

  const { dispatch: dispatchUser } = useContext(StateContext);
  const [username, setUsername] = useState("");
  const [loginFailed, setLoginFailed] = useState(false);
  const [password, setPassword] = useState("");
  const [user, login] = useResource((username, password) => ({
    url: "/login",
    method: "post",
    data: { email: username, password },
  }));
  useEffect(() => {
    if (user) {
      if (user?.data?.user) {
        setLoginFailed(false);
        dispatchUser({ type: "LOGIN", username: user.data.user.email });
      } else {
        setLoginFailed(true);
      }
    }
  }, [user]);

  function handlePassword (evt) { setPassword(evt.target.value); }
  function handleUsername (evt) { setUsername(evt.target.value); }

  return (
    <form 
      onSubmit={ (e) => { e.preventDefault(); dispatchUser({ type: "LOGIN", username }); }}>   

      <label htmlFor="login-username">Username:</label>
      <input type="text" name="login-username" id="login-username" 
          value={username} onChange={handleUsername}/>

      <label htmlFor="login-password">Password:</label>
      <input type="password" value={password} onChange={handlePassword} name="login-username" id="login-username" />
      <input type="submit" value="Login" disabled={username.length === 0}/>

    </form>
  );
}
